let slideIndex=4,timer=4e3,sliderAutoPlay=setInterval("nextSlide()",timer);function restartSlideAutoPlay(){clearInterval(sliderAutoPlay),sliderAutoPlay=setInterval("nextSlide()",timer)}function nextSlide(){showSlides(slideIndex+=1),restartSlideAutoPlay()}function prevSlide(){showSlides(slideIndex-=1),restartSlideAutoPlay()}function currentSlide(e){showSlides(slideIndex=e),restartSlideAutoPlay()}function showSlides(e){const l=document.querySelectorAll(".slider__item"),t=document.querySelectorAll(".slider-nav__item");e>l.length&&(slideIndex=1),e<1&&(slideIndex=l.length);for(let e of l)e.classList.remove("slider__item--active");for(let e of t)e.classList.remove("slider-nav__item--active");l[slideIndex-1].classList.add("slider__item--active"),t[slideIndex-1].classList.add("slider-nav__item--active")}showSlides(slideIndex);